import './App.css';
import React from 'react';
import axios from 'axios';
import Cookies from 'universal-cookie';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({
      apiResponse: "", 
      name: "", 
      lastName: "",
      isLoggedIn: ""
    });

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleLastNameChange = this.handleLastNameChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI = () => {
      var cookies = new Cookies();
      //console.log(cookies.get('name'));
      cookies.set('name', this.state.name, {path: '/'});
      cookies.set('lastName', this.state.lastName, {path: '/'});
      console.log(cookies.get('name'));
      console.log(cookies.get('lastName'));
      const formData = {
        name: this.state.name, 
        lastName: this.state.lastName
      }
      axios
      .post('http://localhost:5000/testAPI', formData)
      .then((response) => {this.state.apiResponse = response.data; console.log(response.data);})
      .then(() => console.log('POST Request sent'))
      .catch(err => {
        console.error(err);
      });
      var {apiResponse} = this.state;
      console.log(this.state.apiResponse);
      if(apiResponse==="1")
        this.setState({isLoggedIn: "Successfully logged in!"});
      else
        this.setState({isLoggedIn: "Error"});
  } 

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleLastNameChange(event) {
    this.setState({lastName: event.target.value});
  }

  handleSubmit(event) {
    alert("Name: " + this.state.name + " Last name: " + this.state.lastName);
    event.preventDefault();
    this.callAPI(); 
  }

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit}>
          <label>
            Name: 
            <input type="text" value={this.state.name} onChange={this.handleNameChange}/>
            Last name:
            <input type="text" value={this.state.value} onChange={this.handleLastNameChange}/>
          </label>
          <input type="submit" value="Submit"/>
        </form>
        <p>{this.state.isLoggedIn}</p>
      </div>
    );
  }
}

export default App;
