import './App.css';
import React from 'react';
import axios from 'axios';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({apiResponse:"", name:"", lastName:""});

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleLastNameChange = this.handleLastNameChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI = () => {
      const formData = {
        name: this.state.name, 
        lastName: this.state.lastName
      }
      axios
      .post('http://localhost:5000/testAPI', formData)
      .then(() => console.log('POST Request sent'))
      .catch(err => {
        console.error(err);
      });
  } 

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleLastNameChange(event) {
    this.setState({lastName: event.target.value});
  }

  handleSubmit(event) {
    alert("Name: " + this.state.name + " Last name: " + this.state.lastName);
    event.preventDefault();

  }

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit}>
          <label>
            Name: 
            <input type="text" value={this.state.name} onChange={this.handleNameChange}/>
            Last name:
            <input type="text" value={this.state.value} onChange={this.handleLastNameChange}/>
          </label>
          <input type="submit" value="Submit"/>
        </form>
        <p>{this.state.apiResponse}</p>
      </div>
    );
  }
}

export default App;
