import './App.css';
import React from 'react';
import axios from 'axios';
import Cookies from 'universal-cookie';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({
      apiResponse: null, 
      apiResponseParsed: "",
      name: "", 
      lastName: "",
      isLoggedIn: ""
    });

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleLastNameChange = this.handleLastNameChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  callAPI = () => {
      var cookies = new Cookies();
      //console.log(cookies.get('name'));
      cookies.set('name', this.state.name, {path: '/'});
      cookies.set('lastName', this.state.lastName, {path: '/'});
      console.log(cookies.get('name'));
      console.log(cookies.get('lastName'));
      const formData = {
        name: this.state.name, 
        lastName: this.state.lastName
      }
      axios
      .post('http://localhost:5000/api', formData)
      .then((response) => {
        this.setState((state) => {
        return {apiResponse: response.data.myCollection}}
        ); 
        console.log("API RESPONSE OVAJ: ", this.state.apiResponse); 
        this.parseAPIResponse();
      })
      .then(() => console.log('POST Request sent'))
      .catch(err => {
        console.error(err);
      }); 
  } 

  parseAPIResponse = () => {
    let parsedResponse = JSON.parse(this.state.apiResponse);
    for(var i=0;i<parsedResponse.length;i++) {
      this.setState((state) => {
        return {apiResponseParsed: this.state.apiResponseParsed+parsedResponse[i].name}}
      );
    }
  }

  updateLoggedInState = () => {
    console.log(this.state.apiResponse);
    this.setState({isLoggedIn : (this.state.apiResponse==1 ? "Logged in" : "Not logged in")});
  }

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleLastNameChange(event) {
    this.setState({lastName: event.target.value});
  }

  handleSubmit(event) {
    alert("Name: " + this.state.name + " Last name: " + this.state.lastName);
    event.preventDefault();
    this.callAPI(); 
  }

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit}>
          <label>
            Name: 
            <input type="text" value={this.state.name} onChange={this.handleNameChange}/>
            Last name:
            <input type="text" value={this.state.value} onChange={this.handleLastNameChange}/>
          </label>
          <input type="submit" value="Submit"/>
        </form>
        <p>{this.state.isLoggedIn}</p>
        <p>{this.state.apiResponseParsed}</p>
      </div>
    );
  }
}

export default App;
